import React, { useState, useEffect } from "react";

const images = [
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSdNQKxRfBCcElxJkb4KpnXRH7Vv6X2XC1uoA&s",
];

const HeroSlider = () => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev === images.length - 1 ? 0 : prev + 1));
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative w-full h-[600px] overflow-hidden">
      {images.map((image, index) => (
        <div
          key={index}
          className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
            index === current ? "opacity-100 z-10" : "opacity-0 z-0"
          }`}
        >
          <img
            src={image}
            alt={`Slide ${index}`}
            className="w-full h-full object-cover"
          />
        </div>
      ))}

      {/* Arrows */}
      <button
        onClick={() =>
          setCurrent((prev) => (prev === 0 ? images.length - 1 : prev - 1))
        }
        className="absolute left-4 top-1/2 -translate-y-1/2 text-white text-3xl bg-black bg-opacity-40 hover:bg-opacity-70 p-2 rounded-full z-20"
      >
        ‹
      </button>
      <button
        onClick={() =>
          setCurrent((prev) => (prev === images.length - 1 ? 0 : prev + 1))
        }
        className="absolute right-4 top-1/2 -translate-y-1/2 text-white text-3xl bg-black bg-opacity-40 hover:bg-opacity-70 p-2 rounded-full z-20"
      >
        ›
      </button>
    </div>
  );
};

export default HeroSlider;
